<template>
    <div>

        <div class="bkh">
            <div style="font-size:1.5rem;">imageSet-upload</div>
            <a href="//yuda-lyu.github.io/w-leaflet-vue/examples/ex-AppIMGImageSetUpload.html" target="_blank" class="item-link">example</a>
            <a href="//github.com/yuda-lyu/w-leaflet-vue/blob/master/docs/examples/ex-AppIMGImageSetUpload.html" target="_blank" class="item-link">code</a>
        </div>

        <div class="bkp">

            <div>
                <button style="margin:0px 3px 3px 0px;" @click="uploadImgjson">upload imgjson</button>
            </div>

            <div style="display:flex; padding-bottom:40px; overflow-x:auto;">

                <div style="position:relative;">
                    <WLeafletVue
                        style="width:800px; height:500px;"
                        :opt="opt"
                    ></WLeafletVue>
                </div>

                <div style="width:600px; min-width:600px; padding:0px 20px;">

                    <div style="border:1px solid #ddd;">
                        <div style="padding-left:5px; overflow-y:auto; height:500px;">
                            <div id="optjson" style="font-size:10pt;"></div>
                        </div>
                    </div>

                </div>

            </div>

        </div>

    </div>
</template>

<script>
import WLeafletVue from './components/WLeafletVue.vue'
import jv from 'w-jsonview-tree'

export default {
    components: {
        WLeafletVue,
    },
    data: function() {
        return {
            'opt': {
                center: [25.04, 121.51],
                zoom: 11,
                imageSets: [
                    {
                        title: 'imageSet A',
                        msg: 'msg from imageSet A',
                        image: {
                            url: `data:image/png;base64,`,
                            lngMin: 121.39,
                            lngMax: 121.65,
                            latMin: 24.94,
                            latMax: 25.14,
                        },
                        visible: true,
                    },
                ],
            },
            'action': [
            ],
        }
    },
    mounted: function() {
        let vo = this
        vo.showOptJson()
    },
    watch: {
        opt: {
            handler: function() {
                let vo = this
                vo.showOptJson()
            },
            deep: true,
        },
    },
    methods: {
        showOptJson: function() {
            let vo = this
            jv(vo.opt, document.querySelector('#optjson'), { expanded: true })
        },
        uploadImgjson: function() {
            let vo = this

            let _ = window._
            let w = window.wsemi

            let kind = 'json'
            w.domShowInputAndGetFilesU8Arrs(kind)
                .then(function(d) {

                    //get first file
                    let file = d[0]
                    // console.log('file', file)

                    //u8a
                    let u8a = file.u8a
                    // console.log('get u8a', Object.prototype.toString.call(u8a), u8a.length)

                    //j
                    let j = w.u8arr2str(u8a)
                    // console.log('j', j)

                    //imgjson
                    let imgjson = JSON.parse(j)
                    // console.log('imgjson', imgjson)

                    //imageSet
                    let imageSet = _.cloneDeep(vo.opt.imageSets[0])
                    imageSet.title = _.get(imgjson, 'name', '')
                    imageSet.msg = _.get(file, 'name', '')
                    imageSet.image = imgjson
                    console.log('imageSet', imageSet)

                    //save
                    vo.opt.imageSets = [imageSet]

                })
                .catch(function(err) {
                    console.log(err)
                })

        },
    },
}
</script>

